<script lang="ts">
	let messages: App.Message[] = [
		{
			content: 'Hello',
			user_id: 'davidnoah'
		},
		{
			content: 'Hello there.',
			user_id: 'redesiigner'
		},
		{
			content: 'stfu',
			user_id: 'muskatnuzz'
		}
	];
	let users: App.User[] = [
		{
			id: 'redesiigner',
			img: `data:image/png;base64,`,
			name: 'Patrick Hellebrand'
		},
		{
			id: 'davidnoah',
			name: 'David Noah'
		},
		{
			id: 'muskatnuzz',
			name: 'Philipp'
		}
	];
	let user = users[0];

	let activeContent: string = 'online';

	const handleContentChange = (content: string) => {
		activeContent = content;
	};

	function sendMessage(event: CustomEvent<string>): void {
		console.log('sendMessage', event.detail);
		// temp
		onMessage({
			content: event.detail,
			user_id: user.id
		});
	}

	function onMessage(message: App.Message): void {
		console.log('onMessage', message);
		message._user = users.find((u) => u.id == message.user_id);
		messages.push(message);
		messages = messages;
	}
</script>

<template>
	<Chat {messages} {users} self={user} on:send={sendMessage}>
		<h1>{$page.params.id}</h1>
	</Chat>
	<div>
